mapper.update:
update food_category
set tag=0
where category_id = #{categoryId,jdbcType=INTEGER}

update food_category
set category = #{category,jdbcType=VARCHAR},
  img_url = #{imgUrl,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=INTEGER},
  tag = #{tag,jdbcType=SMALLINT}
where category_id = #{categoryId,jdbcType=INTEGER}
