mapper.select:
select order_id,img_url,is_remarked,orders.food_id,orders.status,together_date,food_special.name as special_name, orders.order_count as order_count , food.is_discount as is_discount, food.price as price,food.name as name, discount_price from orders,food,food_special where orders.food_id=food.food_id and orders.food_id=food_special.food_id and food_special.special_id =orders.food_special and phone = #{phoneId,jdbcType=VARCHAR} And together_id=#{togetherId,jdbcType=VARCHAR}
and orders.status = #{status,jdbcType=SMALLINT}
and is_remarked = 0
and orders.status !=0
and DATE_SUB(CURDATE(), INTERVAL 10 DAY) <= date(together_date) and orders.tag=1 order by together_date DESC,create_time DESC

SELECT together_id, orders.phone, food. NAME AS NAME, order_count, address, orders.admin_phone, together_date, receiver.phone as receiver_phone, food.price as food_price, orders.price, is_discount, discount_price, users.nickname as admin_name FROM orders, food, receiver, users WHERE orders.food_id = food.food_id AND orders.admin_phone = users.phone AND orders.rank = receiver.rank AND orders.phone = receiver.phone_id AND orders.status=#{status,jdbcType=SMALLINT} AND together_date>=#{time,jdbcType=DATE} and orders.tag=1
and orders.phone like "%${search}%"
order by together_date DESC,orders.phone ASC limit ${offset},${limit}

SELECT together_id, orders.admin_phone, orders.phone, food. NAME AS NAME, order_count, receiver.address, receiver.phone as receiver_phone, orders.admin_phone, food.price as food_price, orders.price, is_discount, discount_price, together_date FROM orders, food, receiver WHERE orders.food_id = food.food_id AND orders.rank = receiver.rank AND orders.phone = receiver.phone_id AND orders.status=1 AND together_date>=#{time,jdbcType=DATE} and orders.tag=1
and orders.phone like "%${search}%"
order by together_date DESC,orders.phone ASC limit ${offset},${limit}

SELECT count(*) FROM orders WHERE together_date>=#{time,jdbcType=DATE} and orders.tag=1
and orders.phone like "%${search}%"
and status=#{status,jdbcType=SMALLINT}
